# --- Data Loading Function ---
@st.cache_data
def load_data(league_name):
    """Loads the cleaned and advanced statistics data for the specified league."""
    try:
        cleaned_file = CLEANED_DATA_FILES.get(league_name, CLEANED_DATA_FILES["Rising Stars S1"])
        advanced_file = ADVANCED_STATS_FILES.get(league_name, ADVANCED_STATS_FILES["Rising Stars S1"])
        
        df_cleaned = pd.read_csv(cleaned_file)
        df_advanced = pd.read_csv(advanced_file)

        # Ensure 'Total_Games_Played' is integer in df_advanced for correct display
        if 'Total_Games_Played' in df_advanced.columns:
            # Fill NaN with 0 before converting to int to avoid errors if some players have no games recorded
            df_advanced['Total_Games_Played'] = df_advanced['Total_Games_Played'].fillna(0).astype(int)
 

        return df_cleaned, df_advanced
    except FileNotFoundError as e:
        st.error(f"Error: Data file not found for {league_name}. Please ensure '{cleaned_file}' and '{advanced_file}' are in the same directory as this script. Run 'gamechanger.py' first to generate these files. Details: {e}")
        return pd.DataFrame(), pd.DataFrame()


